import {css} from "styled-components";

export const mobile = (props) => {
    return css`
        @media only screen and (max-width: 680px){
        ${props}
        }
    `;
};

export const medium = (props) => {
    return css`
        @media only screen and (max-width: 980px){
        ${props}
        }
    `;
};
